import { useState } from 'react';
import './ExpenseForm.css';
function ExpenseForm(props) {
  // const [name, setName] = useState('');
  // const [amount, setAmount] = useState('');
  // const [date, setDate] = useState('');
  const [userInput, setUserInput] = useState({
    enteredName: '',
    enteredAmount: '',
    enteredDate: ''
  });
  function nameChangedHandler(event) {
    setUserInput((prevState) => {
      return { ...prevState, enteredName: event.target.value }
    });
  }
  function amountChangedHandler(event) {
    setUserInput((prevState) => {
      return { ...prevState, enteredAmount: event.target.value }
    });
  }
  function dateChangedHandler(event) {
    setUserInput((prevState) => {
      return { ...prevState, enteredDate: event.target.value }
    });
  }
  function submitHandler(event) {
    event.preventDefault();
    props.onSaveExpense();
  }
  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Name</label>
          <input className='new-expense__control input' value={userInput.enteredName} type="text" onChange={nameChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Amount</label>
          <input className='new-expense__control input' value={userInput.enteredAmount} type="number" min="0.01" step="0.01" onChange={amountChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Date</label>
          <input className='new-expense__control input' value={userInput.enteredDate} type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangedHandler} />
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type="submit">Add New Expense</button>
      </div>
    </form >
  )
}
export default ExpenseForm;
