import { useState } from 'react';
import './ExpenseForm.css';
function ExpenseForm(props) {
  const [name, setName] = useState(props.name);
  function nameChangedHandler(event) {
    setName(event.target.value);
  }
  function amountChangedHandler(event) {
    setName(event.target.value);
  }
  return (
    <form>
      <div className='new-expense__controls'>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Name</label>
          <input className='new-expense__control input' type="text" onChange={nameChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Amount</label>
          <input className='new-expense__control input' type="number" min="0.01" step="0.01" onChange={nameChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Date</label>
          <input className='new-expense__control input' type="date" min="2019-01-01" max="2022-12-31" onChange={nameChangedHandler} />
        </div>
      </div>
      <div className='new-expense__actions'>
        <button>Add New Expense</button>
      </div>
    </form >
  )
}
export default ExpenseForm;
