import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from '../ExpenseFilter/ExpensesFilter';
import { useState } from 'react';
const months = [
  'All Expenses',
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December'
];
function Expenses(props) {
  const [month, setMonth] = useState('All Expenses');
  function filterExpensesBasisMonth(month) {
    setMonth(month);
  }
  const filteredExpenses = props.expenses.filter(result => {
    if (month === 'All Expenses') {
      return true;
    } else if (new Date(result.date).toLocaleString('en-US', { month: 'long' }) === month) {
      return true;
    }
    return false;
  });

  return (
    <Card className='expenses'>
      <ExpensesFilter months={months} dropdownMonths={month} onFilteringMonth={filterExpensesBasisMonth} />
      {filteredExpenses.map((result) => <ExpenseItem date={result.date} name={result.name} amount={result.amount} key={result.name} />)};
    </Card>
  )
}
export default Expenses;
