import './App.css';
import React from 'react';
import Expenses from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense';
const expenses = [
  {
    date: '2020-08-14',
    name: 'Toilet Paper',
    price: 94.12
  },
  {
    date: '2020-03-12',
    name: 'New TV',
    price: 799.49
  },
  {
    date: '2021-03-28',
    name: 'Car Insurance',
    price: 294.67
  },
  {
    date: '2022-06-12',
    name: 'New Desk(Wooden)',
    price: 450
  }
];
function App(props) {
  function saveExpenseData(enteredData) {
    const expenseData = {
      ...enteredData
    }
    expenses.push(expenseData);
  }
  let expensesArray = props.onFilteringYear;
  function filterExpensesBasisYear(year) {
    expensesArray = expensesArray.filter(result => new Date(result.date).toLocaleString('en-US', { year: 'numeric' }) === year);
    return expensesArray;
  }
  return (
    <div>
      <NewExpense onSaveExpense={saveExpenseData} />
      <Expenses expenses={expenses} />
    </div >
  );
}

export default App;
