import './Expenses.css';
import Card from '../UI/Card';
import ExpensesFilter from '../ExpenseFilter/ExpensesFilter';
import ExpensesList from '../ExpensesList/ExpensesList';
function Expenses(props) {
  const [month, setMonth] = useState('All Expenses');
  function filterExpensesBasisMonth(month) {
    setMonth(month);
  }
  return (
    <Card className='expenses'>
      <ExpensesFilter months={months} dropdownMonths={month} onFilteringMonth={filterExpensesBasisMonth} />
      <ExpensesList />
    </Card>
  )
}
export default Expenses;
