import './ExpensesList.css';
import { useReact } from 'react';
const months = [
  'All Expenses',
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December'
];
function ExpensesList(props) {
  const [month, setMonth] = useState('All Expenses');
  function filterExpensesBasisMonth(month) {
    setMonth(month);
  }
  const filteredExpenses = props.expenses.filter(result => {
    if (month === 'All Expenses') {
      return true;
    } else if (new Date(result.date).toLocaleString('en-US', { month: 'long' }) === month) {
      return true;
    }
    return false;
  });
}
export default ExpensesList;
