import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from '../ExpenseFilter/ExpensesFilter';
import { useState } from 'react';
const months = [
  'All Expenses',
  'January',
  'February',
  2022
];
function Expenses(props) {
  const [year, setYear] = useState('All Expenses');
  function filterExpensesBasisYear(year) {
    setYear(year);
  }
  const filteredExpenses = props.expenses.filter(result => {
    if (year === 'All Expenses') {
      return true;
    } else if (new Date(result.date).toLocaleString('en-US', { year: 'numeric' }) === year) {
      return true;
    }
    return false;
  });
  console.log('filteredExpenses:', filteredExpenses);

  return (
    <Card className='expenses'>
      <ExpensesFilter years={years} dropdownYear={year} onFilteringYear={filterExpensesBasisYear} />
      {filteredExpenses.map((result) => <ExpenseItem date={result.date} name={result.name} amount={result.amount} key={result.name} />)};
    </Card>
  )
}
export default Expenses;
