import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from '../ExpenseFilter/ExpensesFilter';
import { useState } from 'react';
const years = [
  'All Expenses',
  2020,
  2021,
  2022
];
function Expenses(props) {
  const [year, setYear] = useState('All Expenses');
  function filterExpensesBasisYear(year) {
    setYear(year);
    // expensesArray = expensesArray.filter(result => new Date(result.date).toLocaleString('en-US', { year: 'numeric' }) === year);
    // for (let i = 0; i < expensesArray.length; i++) {
    //   items.push(<ExpenseItem date={expensesArray[i].date} name={expensesArray[i].name} price={expensesArray[i].price} key={expensesArray[i].name} />)
    // }
  }

  return (
    <Card className='expenses'>
      <ExpensesFilter years={years} dropdownYear={year} onFilteringYear={filterExpensesBasisYear} />
      {props.expenses.map((result) => <ExpenseItem date={result.date} name={result.name} price={result.price} key={result.name} />)};
    </Card>
  )
}
export default Expenses;
