import React from 'react';
import { useState } from 'react';
import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
  const items = [];
  const [yearValue, filterYear] = useState('All Expenses');
  function filterYearHandler(event) {
    console.log('year is:', event.target.value);
    console.log('1:', props.onFilteringYear(event.target.value));
    props.expensesArray = props.onFilteringYear(event.target.value);
    console.log('props.array:', props.expensesArray);
  }
  let year = props.years;
  for (let i = 0; i < year.length; i++) {
    items.push(<option value={year[i].toString()} key={year[i].toString()} > {year[i]}</option >)
  }
  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={filterYearHandler}>
          {items}
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
