const textAlign = {
  'margin': '20px',
  'text-align': 'left',
  'font-weight': 'bold'
}
const h1Align = {
  'margin': '20px',
  'text-align': 'center',
  'font-weight': 'bold'
}
const styleCss = {
  'color': 'red'
}

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      'name': 'MUSTANG',
      'brand': 'FORD',
      'price': 5000000,
      'color': 'Red',
      'year': 1964
    }
  }
  render() {
    return (
      <div className="App" >
        <header className="Devraj-header">
          <p>CAR DETAILS</p>
        </header>
        <p style={h1Align}>
          THE CAR DETAILS ARE AS FOLLOWS:
        </p>
        <p style={textAlign}>Name of the Car is: <span style={styleCss}>{this.state.name}</span></p>
        <p style={textAlign}>Brand of the Car is: <span style={styleCss}>{this.state.brand}</span></p>
        <p style={textAlign}>Price of the Car is: <span style={styleCss}>{this.state.price}</span></p>
        <p style={textAlign}>Color of the Car is: <span style={styleCss}>{this.state.color}</span></p>
        <p style={textAlign}>Year in which this car was made is: <span style={styleCss}>{this.state.year}</span></p>
        <button></button>
      </div>
    );
  }
}

export default App;
