import { useState } from 'react';
import './ExpenseForm.css';
function ExpenseForm(props) {
  // const [name, setName] = useState('');
  // const [amount, setAmount] = useState('');
  // const [date, setDate] = useState('');
  const [enteredData, setEnteredData] = useState({
    name: '',
    amount: '',
    date: ''
  })
  function nameChangedHandler(event) {
    setEnteredData((prevState) => {
      return { ...prevState, name: event.target.value }
    })
  }
  function amountChangedHandler(event) {
    setEnteredData((prevState) => {
      return { ...prevState, amount: event.target.value }
    })
  }
  function dateChangedHandler(event) {
    setEnteredData((prevState) => {
      return { ...prevState, date: event.target.value }
    })
  }
  function submitHandler(event) {
    event.preventDefault();
    const enteredData = {
      name: name,
      amount: amount,
      date: date
    }
    props.onSaveExpense(enteredData);
    setName('');
    setAmount('');
    setDate('');
  }
  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Name</label>
          <input className='new-expense__control input' value={name} type="text" onChange={nameChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Amount</label>
          <input className='new-expense__control input' value={amount} type="number" min="0.01" step="0.01" onChange={amountChangedHandler} />
        </div>
        <div className='new-expense__controls'>
          <label className='new-expense__control label'>Date</label>
          <input className='new-expense__control input' value={date} type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangedHandler} />
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type="submit">Add New Expense</button>
      </div>
    </form >
  )
}
export default ExpenseForm;
