import './ExpensesList.css';
import ExpenseItem from './../Expenses/ExpenseItem';
function ExpensesList(props) {
  const filteredExpenses = props.expenses.filter(result => {
    if (props.month === 'All Expenses') {
      return true;
    } else if (new Date(result.date).toLocaleString('en-US', { month: 'long' }) === props.month) {
      return true;
    }
    return false;
  });
  return (
    <ul className='expenses-list'>
      {
        filteredExpenses.length ? filteredExpenses.map((result) => <ExpenseItem date={result.date} name={result.name} amount={result.amount} key={result.name} />) : <h3 expenses-list__fallback><center>No Expenses found for selected month</center></h3>
      }
    </ul>
  )
}
export default ExpensesList;
