import Chart from './Chart';
function ExpenseChart(props) {
  console.log('props.expenses:', props.expenses);
  let chartData = [
    {
      label: 'Jan',
      count: 0
    },
    {
      label: 'Feb',
      count: 0
    },
    {
      label: 'Mar',
      count: 0
    },
    {
      label: 'Apr',
      count: 0
    },
    {
      label: 'May',
      count: 0
    },
    {
      label: 'Jun',
      count: 0
    },
    {
      label: 'Jul',
      count: 0
    },
    {
      label: 'Aug',
      count: 0
    },
    {
      label: 'Sep',
      count: 0
    },
    {
      label: 'Oct',
      count: 0
    },
    {
      label: 'Nov',
      count: 0
    },
    {
      label: 'Dec',
      count: 0
    }
  ];
  for (const expenses of props.expenses) {
    const month = new Date(expenses.date).getMonth();
    const expenseYear = new Date(expenses.date).getFullYear();
    if (props.year === 'All Expenses') {
      chartData[month].count += expenses.amount;
    } else {
      if (expenseYear.toString() === props.year) {
        console.log('inside if');
        chartData[month].count += expenses.amount;
      }
    }
  }
  return (
    <Chart chartData={chartData} />
  )
}
export default ExpenseChart;
